import { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

const ProductForm = () => {
  //   const [productName, setProductName] = useState("");
  //   const [productPrice, setProductPrice] = useState();
  //   const [imageUrl, setImageUrl] = useState();
  const [productData, setProductData] = useState({
    productName: "",
    productPrice: "",
    imageUrl: "",
  });

  const titleChangeHandler = (event) => {
    // setProductName(event.target.value);
    setProductData({
      ...productData,
      productName: event.target.value,
    });
  };
  const priceChangeHandler = (event) => {
    // setProductPrice(event.target.value);

    setProductData({
      ...productData,
      productPrice: event.target.value,
    });
  };
  const imageChangeHandler = (event) => {
    // setImageUrl(event.target.value);
    setProductData({
      ...productData,
      imageUrl: event.target.value,
    });
  };

  return (
    <Form className="m-3 d-flex flex-column align-items-center justify-content-center">
      <Form.Group className="mb-3 " controlId="formBasicEmail">
        <Form.Label>Ürün Adı: </Form.Label>
        <Form.Control
          type="email"
          placeholder="Ürün Adı Giriniz... "
          onChange={titleChangeHandler}
        />
      </Form.Group>

      <Form.Group className="mb-3 " controlId="formBasicPassword">
        <Form.Label>Ürün Fiyatı:</Form.Label>
        <Form.Control
          type="password"
          placeholder="Ürün Fiyatı Giriniz..."
          onChange={priceChangeHandler}
        />
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Ürün Görseli:</Form.Label>
        <Form.Control
          type="text"
          placeholder="Ürün Görseli Giriniz..."
          onChange={imageChangeHandler}
        />

        <Button variant="danger" type="submit" className=" w-100 mt-3">
          Ürün Ekle
        </Button>
      </Form.Group>
    </Form>
  );
};

export default ProductForm;
